<?php/** * Varietal Settings Help Tab Content */ $tabtext = '';$tabtext .= '<h2>' . __( 'Varietals', 'oenology' ) . '</h2>';$tabtext .= '<p>' . __( 'Varietals are the skins, or styles, applied to Oenology.', 'oenology' ) . '</p>';$option_parameters = oenology_get_option_parameters();$oenology_varietals = $option_parameters['varietal']['valid_options'];foreach ( $oenology_varietals as $varietal ) {	$tabtext .= '<dl>';	$tabtext .= '<dt><strong>' . $varietal['title'] . '</strong></dt>';	$tabtext .= '<dd>' . $varietal['description'] . '</dd>';	$tabtext .= '</dl>';}return $tabtext;?>